        public static void FrameMaker2()
        {

            const int width = 40;
            const int height = 20;

            var map = new int[width, height];

            for (int x = 0; x < map.GetLength(1); x++)
            {
                for (int y = 0; y < map.GetLength(0); y++)
                {
                    Console.Write(map[y, x] + "");
                }
